const t=t=>!!String(t).toLowerCase().match(/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);(()=>{const t=document.querySelectorAll("div.btn"),e=document.getElementById("subscribe-form");for(btn of t)btn.addEventListener("click",(()=>{const t=e.getBoundingClientRect().top+window.scrollY;window.scroll({top:t,behavior:"smooth"})}))})(),(()=>{const e=document.getElementById("subscribe-form"),o=e.querySelector("button");e.querySelector("input").addEventListener("input",(e=>{const n=e.target.value,c=t(n);o.disabled=!c}))})();